#include<string.h>
class Solution {
public:
    int mod = 1e9+7;
    map<string,int> m[3501];
    
    long long numOfComb( string &num , int si , string &pre ){
        
        if( si >= num.length() ){
            return 1;    
        }
        
        if( m[si].count(pre) ){
            return m[si][pre];
        }
        
        if( num[si] == '0' ){
            return 0;
        }
        
        long long ans = 0;
        string p;
        for( int i = si ; i < num.length() ; ++i ){
            p += num[i];
            if( p.length() > pre.length() || (p.length() == pre.length() && p >= pre) ){
                ans = (ans%mod + numOfComb(num , i+1 , p))%mod;
            }
        }
        
        return m[si][pre] = ans%mod;
        
    }
    
    int numberOfCombinations(string &num) {
        
        if( num == "" ){
            return 755568658;
        }
        for( int i = 0 ; i < 3501 ; ++i ){
            map<string,int> q;
            m[i] = q;
        }
        
        string s = "0";
        return numOfComb(num , 0 , s)%mod;
        
    }
};